DROP TABLE IF EXISTS `pass_develop_image_info`;
CREATE TABLE `pass_develop_image_info` (
  `imageCode` int(11) NOT NULL AUTO_INCREMENT COMMENT '镜像编号 主键',
  `imageResource` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '镜像源',
  `imageName` varchar(150) COLLATE utf8_bin DEFAULT NULL,
  `channels` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '渠道',
  `pictureName` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '存放图片名称（包括扩展名）',
  `pictureType` varchar(100) COLLATE utf8_bin DEFAULT NULL,
  `picture` longtext COLLATE utf8_bin COMMENT '图片的存储 使用默认长度（默认最大空间为16m 其他字段不适于存储图片）',
  `simpleIntroduction` longtext COLLATE utf8_bin COMMENT '简介',
  `catagory` varchar(50) COLLATE utf8_bin DEFAULT '' COMMENT '镜像类别类别     ',
  `collectionNumber` int(11) DEFAULT '0' COMMENT '收藏数',
  `downloadNumber` int(11) DEFAULT '0' COMMENT '下载数',
  `createDate` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '新增时间',
  `updateDate` datetime DEFAULT NULL COMMENT '更新人',
  `updateBy` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '更新人',
  `status` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '状态',
  PRIMARY KEY (`imageCode`)
) ENGINE=InnoDB AUTO_INCREMENT=220 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

